import './App.css';
import React, { Component } from 'react';
import Header from "./components/Header";
import axios from "axios";
import ReactBasicTable from "react-basic-table"

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
          userName: "",
          recommended: [],
          ready: false,
        }
    }
    componentDidMount() {
        document.title = "MARS - My Anime Recommendations"
        axios
            .get("http://localhost:8000/api/")
            .then((res) => console.log(res))
            .catch((err) => console.log(err));
    }

    handleInput = event => {
      this.setState({userName: event.target.value});
    }
    logUserName = event => {
      event.preventDefault();
      //console.log(this.state.userName);
      axios
            .post("http://localhost:8000/api/", {userName: this.state.userName})
            .then((res) => {
                /* Array of shows with the corresponding data:
                    name: Anime title
                    main_picture: Main picture for this specific anime, contains url to image
                    synopsis: Anime synopsis
                    animeID: ID of anime on MAL
                    rank: Rank of anime in terms of popularity

                    Not working yet:
                    genres: Only returns IDs of genres, not genre names, will later be fixed
                */
                var rec = JSON.parse(res.data).anime;
                console.log(rec);
                this.setState({
                  recommended: rec,  // Update state variable with results
                  ready: true,
                })
                console.log("Recommended shows:");
                for(var i = 0; i < rec.length; i++) {
                    console.log(rec[i].name);
                }
            })
            .catch((err) => console.log(err));
    };

    render() {
      if (this.state.ready) {
        var cols = ["Relevance"]
        var rows = []
        for(var p in this.recommended) {
          cols.push(p.name.replace(/^\w/, (c) => c.toUpperCase()))
        }
        for(var i=0; i< 20;i++) {
          var entry = [
            <span data-ReactBasicTable-value={"Entry" + i+1}> {i+1} </span>
          ]
          for(var key in this.recommended) {
            entry.push(<span data-ReactBasicTable-value={key+i+1}> {this.recommended[key]} </span>)
          }
          rows.push(entry)
      }}
        return (
            <div className="App">
                <Header />
                <div  className="App-body">
                    <h2>
                        Find Anime Recommendations
                    </h2>
                    <div className="Search-Bar">
                      <input onChange={this.handleInput} placeholder="Enter MAL Username"/>
                      <button onClick={this.logUserName}> Search </button>
                    </div>
                </div>
                <div className="Recommendations">
                  <ReactBasicTable columns={cols} rows={rows}/>
                </div>
            </div>
        );
    }
}

export default App;
